import "./projectList.scss";
import { Fragment, useState, useEffect, useRef } from "react";
import { useDeleteTodoMutation } from "../../utils/firebase/firebase-utils";

function ProjectList(props) {
  const { projectDetails, setProjectDetails } = props;
  const [edit, setEdit] = useState(false);
  const [editProjectId, setEditProjectId] = useState(null);
  const [deleteTodo] = useDeleteTodoMutation();
  const [editAndSave, setEditAndSave] = useState("Edit");
  const projectNameRef = useRef("");

  const editField = (data) => {
    if (data.projectId === editProjectId) {
      return (
        <input
          className="inputProjectField"
          type="text"
          defaultValue={data.projectName}
          ref={projectNameRef}
          onChange={(e) => {
            const updatedProjects = projectDetails.map((project) => {
              if (project.projectId === editProjectId) {
                return { ...project, projectName: e.target.value };
              } else {
                return project;
              }
            });
            setProjectDetails(updatedProjects);
          }}
        />
      );
    } else {
      return <h5>{data.projectName}</h5>;
    }
  };
  const isEditing = (projectId) => projectId === editProjectId;

  const handleEditClick = (projectId) => {
    if (isEditing(projectId)) {
      // Save the changes
      setEditProjectId(null);
    } else {
      // Enable editing for the clicked project
      setEditProjectId(projectId);
    }
  };

  useEffect(() => {
    if (editProjectId === null) {
      setEditAndSave("Edit");
    } else {
      console.log(projectNameRef.current.value);
      setEditAndSave("Save");
    }
  }, [editProjectId]);

  return (
    <Fragment>
      {projectDetails?.map((data) => (
        <div className="project-field" key={data.projectId}>
          <div
            type="submit"
            onClick={() => deleteTodo(data.projectId)}
            className="checkBlock"
          >
            <input className="check" type="checkbox" />
            <button className="deteButton">delete</button>
          </div>
          {editField(data)}
          {/* <h5>{data.projectName}</h5> */}
          <h5>{data.startingDate}</h5>
          <h5>{data.deadLine}</h5>
          <h5>In progress</h5>
          <button
            className="buttonField"
            value={data.id}
            onClick={() => {
              setEditProjectId(data.projectId);
              handleEditClick(data.projectId);
              // setEdit((prevState) => !prevState);
            }}
          >
            {isEditing(data.projectId) ? "Save" : "Edit"}
            {/* {editAndSave} */}
          </button>
          <button className="buttonField">Mark as complete</button>
        </div>
      ))}
    </Fragment>
  );
}

export default ProjectList;
