import { useState, useRef, useMemo } from "react";
import "./project-add.scss";

function ProjectAdd(props) {
  // const [projectName, setProjectName] = useState("");
  const projectNameRef = useRef("");
  const startingDateRef = useRef("");
  const deadLineRef = useRef("");
  const [startingDate, setStartingDate] = useState("");
  const [deadLine, setDeadLine] = useState("");
  const [button, setButton] = useState("Add Project");
  const [showHide, setShowHide] = useState("hide");

  function showHideFunc() {
    if (button === "Add Project") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }

  // const projectName = useMemo(() =>

  console.log("asd");

  function submitHandler(event) {
    event.preventDefault();
    if (showHide === "hide") {
      const projectDetails = {
        projectName: projectNameRef.current.value,
        startingDate: startingDateRef.current.value,
        deadLine: deadLineRef.current.value,
      };
      // console.log(projectDetails);
      props.onSaveProjectDetails(projectDetails);
      projectNameRef.current.value = "";
      startingDateRef.current.value = "";
      deadLineRef.current.value = "";
    } else return;
    // console.log("check");
  }

  return (
    <form onSubmit={submitHandler} className="project-list">
      <div className="input-fields">
        <h4>Project Name</h4>
        <input
          className={showHide}
          required
          ref={projectNameRef}
          type="text"
          placeholder="Project name"
        />
      </div>
      <div className="input-fields">
        <h4>Starting date</h4>
        <input
          className={showHide}
          ref={startingDateRef}
          type="date"
          placeholder="Date"
        ></input>
      </div>
      <div className="input-fields">
        <h4>Dead Line</h4>
        <input
          className={showHide}
          value={deadLine}
          type="date"
          placeholder="Dead line"
          onChange={(event) => {
            setDeadLine(event.target.value);
          }}
        ></input>
      </div>
      <button
        className="addProjectButton"
        type="submit"
        onClick={() => {
          showHideFunc();
          button === "Add Project"
            ? setButton("submit")
            : setButton("Add Project");
        }}
      >
        {button}
      </button>
    </form>
  );
}

export default ProjectAdd;
