import "./sign-up.scss";
import { Fragment, useState } from "react";
import { createAuthUserWithEmailAndPassword } from "../../utils/firebase-utils";

const defaultFormFields = {
  fullName: "",
  email: "",
  phone: "",
  password: "",
  confirmPassword: "",
};

function SignUp() {
  const [showHide, setShowHide] = useState("hide");
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { fullName, email, phone, password, confirmPassword } = formFields;

  console.log(formFields);
  function handleChange(event) {
    const { name, value } = event.target;
    setFormFields({ ...formFields, [name]: value });
  }

  function popup() {
    if (showHide === "hide") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }

  return (
    <Fragment>
      <div className="log-button" onClick={popup}>
        Sign-up
      </div>
      <div className={`${showHide}`}>
        <div className="popup">
          <form onSubmit={() => {}}>
            <h1>I don't have an account</h1>
            <h5>Sign up with your email and password</h5>
            <div className="inputBlock">
              <label>First and Last name</label>
              <input
                type="text"
                required
                onChange={handleChange}
                name="fullName"
                value={fullName}
              ></input>
            </div>
            <div className="inputBlock">
              <label>Email</label>
              <input
                type="email"
                onChange={handleChange}
                name="email"
                value={email}
              ></input>
            </div>
            <div className="inputBlock">
              <label>Phone</label>
              <input
                type="number"
                onChange={handleChange}
                name="phone"
                value={phone}
              ></input>
            </div>
            <div className="inputBlock">
              <label>Password</label>
              <input
                type="password"
                onChange={handleChange}
                name="password"
                value={password}
              ></input>
            </div>
            <div className="inputBlock">
              <label>Confirm Password</label>
              <input
                type="password"
                onChange={handleChange}
                name="confirmPassword"
                value={confirmPassword}
              ></input>
            </div>
            <button className="submitbutton" type="submit" onClick={popup}>
              SUBMIT
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
}
export default SignUp;
