import "./project-page.scss";
import { db } from "../../utils/firebase/firebase-utils";
import { collection, addDoc } from "firebase/firestore";

import { useState, useContext, useEffect } from "react";
import ProjectAdd from "../projectAdd-field/project-add";
import ProjectField from "../projectField/projectField";
import { ProjectsContext, UserContext } from "../../contexts/context";

function ProjectPage() {
  const [projectDetails, setProjectDetails] = useState([]);
  const { currentUser } = useContext(UserContext);
  const { currentProject } = useContext(ProjectsContext);
  const userId = currentUser.uid;

  useEffect(() => {
    userCheck();
  }, [currentUser]);

  function userCheck() {
    if (currentUser) {
      setTimeout(() => {
        getProjects();
      }, "1500");
    } else {
      setProjectDetails([]);
      return;
    }
  }

  function getProjects() {
    setProjectDetails(
      currentProject.map((e) => ({
        id: Math.random().toString(),
        name: e.projectName,
        date: e.startingDate,
        deadLine: e.deadLine,
      }))
    );
  }

  const createProject = async () => {
    try {
      const projectId = projectData.name;

      // const projectData = {
      //   name: "My Project",
      //   description: "This is my project",
      // };

      // Add the project data to the "projects" collection in Firestore
      const docRef = await addDoc(
        collection(db, `users/${userId}/projects`),
        projectData
      );
      console.log("Project created with ID:", docRef.id);
    } catch (error) {
      console.error("Error creating project:", error);
    }
  };

  const onSaveProjectDetailsHandler = (inputProjectDetails) => {
    setProjectDetails((prevDetails) => [
      ...prevDetails,
      {
        ...inputProjectDetails,
        id: Math.random().toString(),
      },
    ]);
  };

  // console.log(projectDetails);
  return (
    <div className="project-container">
      <div className="project-menu">
        <div>sort by</div>
        <div>sort by</div>
        <div>sort by</div>
        <div>something</div>
        {/* <div className="button">Add project</div> */}
      </div>
      <ProjectAdd onSaveProjectDetails={onSaveProjectDetailsHandler} />
      <ProjectField projectDetails={projectDetails} />
      <button onClick={createProject}>Create Project</button>
    </div>
  );
}

export default ProjectPage;
