import "./project-page.scss";
import { useState, useContext, useEffect } from "react";
import ProjectAdd from "../projectAdd-field/project-add";
import ProjectField from "../projectField/projectField";
import { ProjectsContext, UserContext } from "../../contexts/context";

function ProjectPage() {
  const [projectDetails, setProjectDetails] = useState([]);
  const { currentUser } = useContext(UserContext);
  const { currentProject, setCurrentProject } = useContext(ProjectsContext);

  // console.log(projectDetails);

  useEffect(() => {
    userCheck();
  }, [currentUser]);

  function userCheck() {
    setTimeout(() => {
      if (currentUser) {
        console.log("user here");
        getProjects();
      } else {
        setCurrentProject([]);
        console.log("no user");

        return;
      }
    }, "1500");
  }

  function getProjects() {
    setProjectDetails(
      currentProject.map((e) => ({
        id: Math.random().toString(),
        name: e.projectName,
        date: e.startingDate,
        deadLine: e.deadLine,
      }))
    );
  }

  const onSaveProjectDetailsHandler = (inputProjectDetails) => {
    // setProjectDetails((prevDetails) => [
    //   ...prevDetails,
    //   {
    //     ...inputProjectDetails,
    //     id: Math.random().toString(),
    //   },
    // ]);
  };

  // console.log(projectDetails);
  return (
    <div className="project-container">
      <div className="project-menu">
        <div>sort by</div>
        <div>sort by</div>
        <div>sort by</div>
        <div>something</div>
        {/* <div className="button">Add project</div> */}
      </div>
      <ProjectAdd onSaveProjectDetails={onSaveProjectDetailsHandler} />
      <ProjectField projectDetails={projectDetails} />
    </div>
  );
}

export default ProjectPage;
