import "./projectList.scss";
import { Fragment } from "react";
import { useDeleteTodoMutation } from "../../utils/firebase/firebase-utils";

function ProjectList(props) {
  const [deleteTodo] = useDeleteTodoMutation();

  const editProject = (data) => {
    console.log(data);
  };

  return (
    <Fragment>
      {props.projectDetails?.map((data) => (
        <div className="project-field" key={data.projectId}>
          <div
            type="submit"
            onClick={() => deleteTodo(data.projectId)}
            className="checkBlock"
          >
            <input className="check" type="checkbox" />
            <button className="deteButton">delete</button>
          </div>
          <h5>{data.projectName}</h5>
          <h5>{data.startingDate}</h5>
          <h5>{data.deadLine}</h5>
          <h5>In progress</h5>
          <button className="buttonField" value={data.id} onClick={editProject}>
            Edit
          </button>
          <button className="buttonField">Mark as complete</button>
        </div>
      ))}
    </Fragment>
  );
}

export default ProjectList;
