function ProjectAdd() {
  return (
    <form onSubmit={submitHandler} className="project-list">
      <div className="input-fields">
        <h5>Project Name</h5>
        <input
          className={showHide}
          type="text"
          placeholder="Project name"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
      </div>
      <div className="input-fields">
        <label>Starting date</label>
        <input
          className={showHide}
          type="date"
          placeholder="Date"
          onChange={(event) => {
            setDate(event.target.value);
          }}
        ></input>
      </div>
      <div className="input-fields">
        <label>Dead Line</label>
        <input
          className={showHide}
          type="date"
          placeholder="Dead line"
          onChange={(event) => {
            setDeadLine(event.target.value);
          }}
        ></input>
      </div>
      <button
        type="submit"
        onClick={() => {
          showHideFunc();
          button === "Add Project"
            ? setButton("submit")
            : setButton("Add Project");
        }}
      >
        {button}
      </button>
    </form>
  );
}

export default ProjectAdd;
