import { useState, useRef, useContext, useEffect } from "react";
import ProjectList from "../projectList/projectList";
import { UserContext } from "../../contexts/context";
import {
  useGetTodosQuery,
  useAddTodoMutation,
} from "../../utils/firebase/firebase-utils";
import "./project-add.scss";

const now = new Date();
const year = now.getFullYear();
const month = String(now.getMonth() + 1).padStart(2, "0");
const day = String(now.getDate()).padStart(2, "0");
// const hours = String(now.getHours()).padStart(2, "0");
// const minutes = String(now.getMinutes()).padStart(2, "0");
// const seconds = String(now.getSeconds()).padStart(2, "0");
const formattedDate = `${day}-${month}-${year} `;

function ProjectAdd() {
  //////////////////////////////////
  const projectNameRef = useRef("");
  const startingDateRef = useRef("");
  const deadLineRef = useRef("");
  const [button, setButton] = useState("Add Project");
  const [showHide, setShowHide] = useState("hide");
  /////////////////////////////////////////////////

  const { currentUserId, setCurrentUserId } = useContext(UserContext);

  ////////////////////////////////////////////
  const { refetch } = useGetTodosQuery(currentUserId, {
    skip: !currentUserId, // Enable the query when userId is truthy
  });
  const [addTodo] = useAddTodoMutation();

  function onSaveProjectDetailsHandler(inputProjectDetails) {
    let projectData = {
      projectName: inputProjectDetails.projectName,
      startingDate: formattedDate,
      deadLine: inputProjectDetails.deadLine,
    };
    setTimeout(() => {
      refetch();
    }, 400);
    addTodo(projectData);
  }

  /////////////////////////////////////
  function showHideFunc() {
    if (button === "Add Project") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }
  ////////////////////////

  function submitHandler(event) {
    event.preventDefault();
    if (showHide === "hide") {
      const projectDetails = {
        projectName: projectNameRef.current.value,
        startingDate: formattedDate,
        deadLine: deadLineRef.current.value,
      };
      // console.log(projectDetails);
      onSaveProjectDetailsHandler(projectDetails);
      projectNameRef.current.value = "";
      // startingDateRef.current.value = "";
      deadLineRef.current.value = "";
    } else return;
    // console.log("check");
  }

  const formatDate = (dateString) => {
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  ////////////////////////////////////////////////////////////
  return (
    <>
      <form onSubmit={submitHandler} className="project-list">
        <div className="input-fields">
          <h4>Project Name</h4>
          <input
            className={showHide}
            required
            ref={projectNameRef}
            type="text"
            placeholder="Project name"
          />
        </div>
        <div className="input-fields">
          <h4>Starting date</h4>
          <h5 className={showHide}>{formattedDate}</h5>
        </div>
        <div className="input-fields">
          <h4>Dead Line</h4>
          <input
            className={showHide}
            ref={deadLineRef}
            type="date"
            placeholder="Dead line"
            onChange={(e) => {
              const formattedDate = formatDate(e.target.value);
              deadLineRef.current.value = formattedDate;
            }}
          ></input>
        </div>
        <button
          className="addProjectButton"
          type="submit"
          onClick={() => {
            showHideFunc();
            button === "Add Project"
              ? setButton("submit")
              : setButton("Add Project");
          }}
        >
          {button}
        </button>
      </form>
      <ProjectList
      // projectDetails={projectDetails}
      />
    </>
  );
}

export default ProjectAdd;
