          async function getRefProject() {
            return `https://firestore.googleapis.com/v1/projects/gotask-973a8/databases/(default)/documents/users/${userId}/projects`;
          }

          async function fetchData() {
            const url = await getRefProject();
            const response = await fetch(url);
            const data = await response.json();
            // Process the fetched data here
            console.log(data);
          }

          fetchData();

