import "./projectList.scss";
import { Fragment, useState } from "react";
import { useDeleteTodoMutation } from "../../utils/firebase/firebase-utils";

function ProjectList(props) {
  const [deleteTodo] = useDeleteTodoMutation();
  const [editMode, setEditMode] = useState(false);
  const [editedProjectName, setEditedProjectName] = useState("");

  const editProject = (data) => {
    console.log(e);
    setEditMode(true);
    setEditedProjectName(data.projectName);
  };

  const handleProjectNameChange = (e) => {
    setEditedProjectName(e.target.value);
  };

  const saveProjectName = (data) => {
    // Perform the logic to save the edited project name
    console.log("Saving project name:", editedProjectName);
    setEditMode(false);
    // Update the project name in the backend/database
  };

  return (
    <Fragment>
      {props.projectDetails?.map((data) => (
        <div className="project-field" key={data.projectId}>
          <div
            type="submit"
            onClick={() => deleteTodo(data.projectId)}
            className="checkBlock"
          >
            <input className="check" type="checkbox" />
            <button className="deteButton">delete</button>
          </div>
          {editMode ? (
            <input
              type="text"
              value={editedProjectName}
              onChange={handleProjectNameChange}
            />
          ) : (
            <h5>{data.projectName}</h5>
          )}
          <h5>{data.startingDate}</h5>
          <h5>{data.deadLine}</h5>
          <h5>In progress</h5>
          <button
            className="buttonField"
            value={data.id}
            onClick={() => editProject(data)}
          >
            Edit
          </button>
          {editMode && (
            <button
              className="buttonField"
              onClick={() => saveProjectName(data)}
            >
              Save
            </button>
          )}
          <button className="buttonField">Mark as complete</button>
        </div>
      ))}
    </Fragment>
  );
}

export default ProjectList;
