import "./project-page.css";
import { useState } from "react";
import { ProjectAdd } from "../projectAdd-field/project-add";

function ProjectPage() {
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [deadLine, setDeadLine] = useState("");

  const [button, setButton] = useState("Add Project");
  const [showHide, setShowHide] = useState("hide");
  function showHideFunc() {
    if (button === "Add Project") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }

  function submitHandler(event) {
    event.preventDefault();

    const projectDetails = {
      name: name,
      date: date,
      deadLine: deadLine,
    };
    console.log(projectDetails);
  }

  return (
    <div className="project-container">
      <div className="project-menu">
        <div>sort by</div>
        <div>Add new project</div>
        <div className="button">Add project</div>
      </div>

      {/*                                               */}

      <ProjectAdd />

      {/*                                               */}

      <div className="project-list">
        <h5>Project name</h5>
        <h5>Date</h5>
        <h5>stage in working or completed</h5>
        <h5>Dead line</h5>
      </div>
    </div>
  );
}

export default ProjectPage;
