import "./sign-up.scss";
import { Fragment, useState } from "react";

const defaultFormFields = {
  fullName: "",
  email: "",
  phone: "",
  password: "",
  confirmPassword: "",
};

function SignUp() {
  const [showHide, setShowHide] = useState("hide");
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { fullName, email, phone, password, confirmPassword } = formFields;

  function handleChange(event) {
    const { name } = event;
  }
  function popup() {
    if (showHide === "hide") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }

  return (
    <Fragment>
      <div className="log-button" onClick={popup}>
        Sign-up
      </div>
      <div className={`${showHide}`}>
        <div className="popup">
          <form onSubmit={() => {}}>
            <h1>I don't have an account</h1>
            <h5>Sign up with your email and password</h5>
            <div className="inputBlock">
              <label>First and Last name</label>
              <input type="text" required onChange={handleChange}></input>
            </div>
            <div className="inputBlock">
              <label>Email</label>
              <input
                type="email"
                onChange={handleChange}
                name="fullName"
              ></input>
            </div>
            <div className="inputBlock">
              <label>Phone</label>
              <input type="number" onChange={handleChange}></input>
            </div>
            <div className="inputBlock">
              <label>Password</label>
              <input type="password" onChange={handleChange}></input>
            </div>
            <div className="inputBlock">
              <label>Confirm Password</label>
              <input type="password" onChange={handleChange}></input>
            </div>
            <button className="submitbutton" type="submit" onClick={popup}>
              SUBMIT
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
}
export default SignUp;
