import "./project-page.css";
import ProjectAdd from "../projectAdd-field/project-add";
import { useState } from "react";

function ProjectPage() {
  const [projectDetails, setProjectDetails] = useState("");
  const [projectsItems, setProjectsItems] = useState({});

  const onSaveProjectDetailsHandler = (inputProjectDetails) => {
    setProjectDetails({
      ...inputProjectDetails,
      id: Math.random().toString(),
    });
    setProjectsItems(inputProjectDetails.map());
  };
  console.log(projectDetails);

  // function addProjectToTheList(projectDetails) {}

  return (
    <div className="project-container">
      <div className="project-menu">
        <div>sort by</div>
        <div>Add new project</div>
        <div className="button">Add project</div>
      </div>

      {/*                                               */}

      <ProjectAdd onSaveProjectDetails={onSaveProjectDetailsHandler} />

      {/*                                               */}

      <div className="project-list">
        <h5>{projectDetails.name}</h5>
        <h5>{projectDetails.date}</h5>
        <h5>{projectDetails.deadLine}</h5>
        <h5>In progress</h5>
      </div>
    </div>
  );
}

export default ProjectPage;
