import { useState, useRef, useMemo } from "react";
import "./project-add.scss";

function ProjectAdd(props) {
  const projectNameRef = useRef("");
  const startingDateRef = useRef("");
  const deadLineRef = useRef("");
  const [button, setButton] = useState("Add Project");
  const [showHide, setShowHide] = useState("hide");
  /////////////////////////////////////////////////

  const [projectDetails, setProjectDetails] = useState([]);
  const { currentUserId, setCurrentUserId } = useContext(UserContext);
  const [rerender, setRerender] = useState(1);

  //////////////////////////////////////////
  const {
    data: projects,
    isLoading,
    isSuccess,
    isError,
    error,
    refetch,
    // refetch,
  } = useGetTodosQuery(currentUserId, {
    skip: !currentUserId, // Enable the query when userId is truthy
  });
  const [addTodo] = useAddTodoMutation();
  const [updateTodo] = useUpdateTodoMutation();
  const [deleteTodo] = useDeleteTodoMutation();
  //////////////////////////////////////////

  useEffect(() => {
    if (currentUserId && projects && isSuccess) {
      // setUserId(currentUserId.uid);
      const arr = projects.documents.map((project) => {
        const { deadLine, projectId, projectName, startingDate } =
          project?.fields || {};

        return {
          deadLine: deadLine?.stringValue || "",
          projectId: projectId?.stringValue || "",
          projectName: projectName?.stringValue || "",
          startingDate: startingDate?.stringValue || "",
        };
      });

      setProjectDetails(arr);
    }
  }, [currentUserId, projects]);

  //////////////////////////////////////////////////
  useEffect(() => {
    if (!currentUserId) {
      setProjectDetails([]);
    }
  }, [currentUserId]);
  ///////////////////////////////////////////////////////

  function onSaveProjectDetailsHandler(inputProjectDetails) {
    let projectData = {
      projectName: inputProjectDetails.projectName,
      startingDate: inputProjectDetails.startingDate,
      deadLine: inputProjectDetails.deadLine,
    };
    setTimeout(() => {
      refetch();
    }, 400);
    addTodo(projectData);
  }

  /////////////////////////////////////
  function showHideFunc() {
    if (button === "Add Project") {
      setShowHide("show");
    } else {
      setShowHide("hide");
    }
  }
  ////////////////////////

  function submitHandler(event) {
    event.preventDefault();
    if (showHide === "hide") {
      const projectDetails = {
        projectName: projectNameRef.current.value,
        startingDate: startingDateRef.current.value,
        deadLine: deadLineRef.current.value,
      };
      // console.log(projectDetails);
      props.onSaveProjectDetails(projectDetails);
      projectNameRef.current.value = "";
      startingDateRef.current.value = "";
      deadLineRef.current.value = "";
    } else return;
    // console.log("check");
  }

  ////////////////////////////////////////////////////////////
  return (
    <form onSubmit={submitHandler} className="project-list">
      <div className="input-fields">
        <h4>Project Name</h4>
        <input
          className={showHide}
          required
          ref={projectNameRef}
          type="text"
          placeholder="Project name"
        />
      </div>
      <div className="input-fields">
        <h4>Starting date</h4>
        <input
          className={showHide}
          ref={startingDateRef}
          type="date"
          placeholder="Date"
        ></input>
      </div>
      <div className="input-fields">
        <h4>Dead Line</h4>
        <input
          className={showHide}
          ref={deadLineRef}
          type="date"
          placeholder="Dead line"
        ></input>
      </div>
      <button
        className="addProjectButton"
        type="submit"
        onClick={() => {
          showHideFunc();
          button === "Add Project"
            ? setButton("submit")
            : setButton("Add Project");
        }}
      >
        {button}
      </button>
    </form>
  );
}

export default ProjectAdd;
